INSERT INTO wp_wc_admin_notes VALUES("21","ppxo-pps-upgrade-paypal-payments-1","info","en_US","Get the latest PayPal extension for WooCommerce","Heads up! Theres a new PayPal on the block!<br /><br />Now is a great time to upgrade to our latest <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal extension</a> to continue to receive support and updates with PayPal.<br /><br />Get access to a full suite of PayPal payment methods, extensive currency and country coverage, and pay later options with the all-new PayPal extension for WooCommerce.","{}","pending","woocommerce.com","2022-05-15 20:14:44","","0","plain","","0","0","info");
